import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';
import { SongStyle } from '@/lib/lyrics';
import { createOpenRouterClient } from '@/lib/openrouter';
import { createSunoClient } from '@/lib/suno';

interface GenerateSongRequest {
  story: string;
  style: SongStyle;
}

export async function POST(request: NextRequest) {
  try {
    const body: GenerateSongRequest = await request.json();
    const { story, style } = body;

    if (!story || story.trim().length < 10) {
      return NextResponse.json(
        { success: false, error: 'Story is too short' },
        { status: 400 }
      );
    }

    const validStyles: SongStyle[] = ['sad', 'savage', 'healing', 'vibe', 'meme'];
    if (!validStyles.includes(style)) {
      return NextResponse.json(
        { success: false, error: 'Invalid style selected' },
        { status: 400 }
      );
    }

    console.log('Step 1: Generating song prompt with OpenRouter...');

    let promptResult;
    try {
      const openRouterClient = createOpenRouterClient();
      promptResult = await openRouterClient.generateSongPrompt({
        extractedText: story,
        style,
      });

      if (!promptResult.prompt || promptResult.prompt.length < 20) {
        throw new Error('Generated lyrics are too short');
      }

      console.log('Step 2: Prompt generated:', promptResult.title);
    } catch (promptError) {
      console.error('OpenRouter prompt generation failed:', promptError);

      promptResult = {
        title: `${style.charAt(0).toUpperCase() + style.slice(1)} HeartHeal Song`,
        tags: `${style}, emotional, heartbreak, healing`,
        prompt: `A ${style} song about heartbreak, emotional healing, and moving forward.\n${story.substring(0, 200)}`,
      };

      console.log('Using fallback prompt template');
    }

    let previewUrl = '';
    let fullUrl = '';
    let lyrics = promptResult.prompt;
    let duration = 30;

    const sunoClient = createSunoClient();

    try {
      console.log('Step 3: Generating music with Suno AI...');

      const musicResult = await sunoClient.generateSong({
        prompt: promptResult.prompt,
        title: promptResult.title,
        tags: promptResult.tags,
        style,
      });

      previewUrl = musicResult.audioUrl;
      fullUrl = musicResult.videoUrl || musicResult.audioUrl;
      duration = musicResult.duration || 60;

      if (musicResult.lyrics && musicResult.lyrics.length > lyrics.length) {
        lyrics = musicResult.lyrics;
      }

      console.log('Music generated successfully');
    } catch (musicError) {
      console.warn('Suno music generation failed, using placeholder:', musicError);

      previewUrl = '/audio/placeholder-preview.mp3';
      fullUrl = '/audio/placeholder-full.mp3';
      duration = 10;
    }

    const song = await prisma.song.create({
      data: {
        title: promptResult.title,
        story,
        style,
        lyrics: lyrics,
        genre: promptResult.tags,
        mood: style,
        previewUrl,
        fullUrl,
        isPurchased: false,
      },
    });

    return NextResponse.json({
      success: true,
      songId: song.id,
      title: promptResult.title,
      lyrics: lyrics,
      message: 'Song generated successfully',
    });
  } catch (error) {
    console.error('Error generating song:', error);
    return NextResponse.json(
      {
        success: false,
        error: error instanceof Error ? error.message : 'Failed to generate song',
      },
      { status: 500 }
    );
  }
}

components/SocialShareButtons.tsx.content:
"use client";

import { FaTiktok, FaInstagram, FaWhatsapp, FaTwitter, FaLink } from "react-icons/fa";
import { motion } from "framer-motion";
import { useState } from "react";

interface SocialShareButtonsProps {
  songId?: string;
  songTitle?: string;
  url?: string;
  title?: string;
  message?: string;
}

export function SocialShareButtons({ songId, songTitle, url, title, message }: SocialShareButtonsProps) {
  const [copied, setCopied] = useState(false);

  const shareUrl = url || (typeof window !== "undefined" && songId
    ? `${window.location.origin}/share/${songId}`
    : "");

  const shareText = message || `Check out my AI-generated HeartHeal song: "${title || songTitle}" üíîüéµ`;

  const handleTikTok = () => {
    const tiktokUrl = `https://www.tiktok.com/upload?caption=${encodeURIComponent(shareText + " " + shareUrl)}`;
    window.open(tiktokUrl, "_blank");
  };

  const handleInstagram = () => {
    window.open("https://www.instagram.com/", "_blank");
    navigator.clipboard.writeText(shareUrl);
  };

  const handleWhatsApp = () => {
    const whatsappUrl = `https://wa.me/?text=${encodeURIComponent(shareText + " " + shareUrl)}`;
    window.open(whatsappUrl, "_blank");
  };

  const handleTwitter = () => {
    const twitterUrl = `https://twitter.com/intent/tweet?text=${encodeURIComponent(shareText)}&url=${encodeURIComponent(shareUrl)}`;
    window.open(twitterUrl, "_blank");
  };

  const handleCopyLink = async () => {
    try {
      await navigator.clipboard.writeText(shareUrl);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error("Failed to copy:", err);
    }
  };

  const socialButtons = [
    {
      name: "TikTok",
      icon: FaTiktok,
      onClick: handleTikTok,
      color: "bg-black hover:bg-gray-800",
    },
    {
      name: "Instagram",
      icon: FaInstagram,
      onClick: handleInstagram,
      color: "bg-gradient-to-r from-purple-500 via-pink-500 to-orange-500 hover:opacity-90",
    },
    {
      name: "WhatsApp",
      icon: FaWhatsapp,
      onClick: handleWhatsApp,
      color: "bg-green-500 hover:bg-green-600",
    },
    {
      name: "Twitter",
      icon: FaTwitter,
      onClick: handleTwitter,
      color: "bg-blue-400 hover:bg-blue-500",
    },
  ];

  return (
    <div className="space-y-4">
      <h3 className="font-semibold text-lg text-gray-700">Share Your Song</h3>

      <div className="flex flex-wrap gap-3">
        {socialButtons.map((button) => {
          const Icon = button.icon;
          return (
            <motion.button
              key={button.name}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={button.onClick}
              className={`${button.color} text-white px-6 py-3 rounded-full flex items-center space-x-2 shadow-lg transition-all duration-300`}
            >
              <Icon className="text-xl" />
              <span className="font-medium">{button.name}</span>
            </motion.button>
          );
        })}

        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={handleCopyLink}
          className="bg-gray-200 hover:bg-gray-300 text-gray-700 px-6 py-3 rounded-full flex items-center space-x-2 shadow-lg transition-all duration-300"
        >
          <FaLink className="text-xl" />
          <span className="font-medium">{copied ? "Copied!" : "Copy Link"}</span>
        </motion.button>
      </div>
    </div>
  );
}

