'use client';

import { useState, useRef } from 'react';
import { FiUpload, FiX, FiImage } from 'react-icons/fi';
import { motion, AnimatePresence } from 'framer-motion';

interface FileUploadProps {
  onFileSelect: (file: File) => void;
  onClear?: () => void;
  acceptedFormats?: string;
  maxSizeMB?: number;
}

export default function FileUpload({
  onFileSelect,
  onClear,
  acceptedFormats = 'image/*',
  maxSizeMB = 10,
}: FileUploadProps) {
  const [isDragging, setIsDragging] = useState(false);
  const [preview, setPreview] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleDragEnter = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
  };

  const validateFile = (file: File): boolean => {
    setError(null);

    if (!file.type.startsWith('image/')) {
      setError('Please upload an image file üì∏');
      return false;
    }

    const maxSizeBytes = maxSizeMB * 1024 * 1024;
    if (file.size > maxSizeBytes) {
      setError(`File too big! Keep it under ${maxSizeMB}MB üî•`);
      return false;
    }

    return true;
  };

  const processFile = (file: File) => {
    if (!validateFile(file)) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      setPreview(e.target?.result as string);
      onFileSelect(file);
    };
    reader.readAsDataURL(file);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);

    const files = e.dataTransfer.files;
    if (files && files.length > 0) {
      processFile(files[0]);
    }
  };

  const handleFileInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      processFile(files[0]);
    }
  };

  const handleClear = () => {
    setPreview(null);
    setError(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
    onClear?.();
  };

  const handleClick = () => {
    fileInputRef.current?.click();
  };

  return (
    <div className="w-full">
      <AnimatePresence mode="wait">
        {!preview ? (
          <motion.div
            key="upload"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            className={`
              relative border-3 border-dashed rounded-xl p-8 md:p-12 text-center cursor-pointer
              transition-all duration-300 bg-exroast-black/50
              ${isDragging ? 'border-exroast-pink bg-exroast-pink/10' : 'border-gray-700'}
              ${error ? 'border-red-500' : ''}
            `}
            onDragEnter={handleDragEnter}
            onDragLeave={handleDragLeave}
            onDragOver={handleDragOver}
            onDrop={handleDrop}
            onClick={handleClick}
          >
            <input
              ref={fileInputRef}
              type="file"
              accept={acceptedFormats}
              onChange={handleFileInput}
              className="hidden"
            />

            <motion.div
              animate={isDragging ? { scale: 1.1 } : { scale: 1 }}
              className="flex flex-col items-center gap-4"
            >
              <div className="w-16 h-16 rounded-full bg-exroast-pink/20 flex items-center justify-center">
                <FiUpload className="w-8 h-8 text-exroast-pink" />
              </div>

              <div>
                <h3 className="text-lg font-black text-white mb-2">
                  {isDragging ? 'Drop the receipts! üî•' : 'Drop Those Receipts üì∏'}
                </h3>
                <p className="text-exroast-gold text-sm mb-1 font-bold">
                  Drag & drop or click to upload
                </p>
                <p className="text-gray-400 text-xs">
                  WhatsApp, iMessage, Instagram DMs (Max {maxSizeMB}MB)
                </p>
              </div>

              <div className="flex items-center gap-2 text-xs text-gray-500 font-bold">
                <FiImage className="w-4 h-4" />
                <span>PNG, JPG, JPEG, WebP</span>
              </div>
            </motion.div>
          </motion.div>
        ) : (
          <motion.div
            key="preview"
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.95 }}
            className="relative"
          >
            <div className="relative rounded-xl overflow-hidden border-2 border-exroast-gold bg-exroast-black">
              <img
                src={preview}
                alt="Chat screenshot preview"
                className="w-full h-auto max-h-96 object-contain"
              />

              <button
                onClick={(e) => {
                  e.stopPropagation();
                  handleClear();
                }}
                className="absolute top-3 right-3 w-10 h-10 bg-exroast-pink hover:bg-red-600 text-white rounded-full flex items-center justify-center transition-colors shadow-lg"
                aria-label="Remove image"
              >
                <FiX className="w-6 h-6" />
              </button>
            </div>

            <p className="text-center text-sm text-exroast-gold mt-3 font-bold">
              Receipts uploaded! Ready to roast üî•
            </p>
          </motion.div>
        )}
      </AnimatePresence>

      {error && (
        <motion.p
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          className="text-red-400 text-sm mt-3 text-center font-bold"
        >
          {error}
        </motion.p>
      )}
    </div>
  );
}

components/LoadingProgress.tsx.content:
'use client';

import { motion } from 'framer-motion';
import { FiMusic, FiImage, FiEdit, FiCheck } from 'react-icons/fi';

export type LoadingStep = 'ocr' | 'lyrics' | 'music' | 'complete';

interface LoadingProgressProps {
  currentStep: LoadingStep;
  progress?: number;
}

const steps = [
  {
    id: 'ocr' as LoadingStep,
    label: 'Extracting Text',
    icon: FiImage,
    description: 'Reading your chat screenshot...',
  },
  {
    id: 'lyrics' as LoadingStep,
    label: 'Writing Lyrics',
    icon: FiEdit,
    description: 'Crafting your breakup song...',
  },
  {
    id: 'music' as LoadingStep,
    label: 'Creating Music',
    icon: FiMusic,
    description: 'Generating your song...',
  },
  {
    id: 'complete' as LoadingStep,
    label: 'Complete',
    icon: FiCheck,
    description: 'Your song is ready!',
  },
];

export default function LoadingProgress({ currentStep, progress = 0 }: LoadingProgressProps) {
  const currentStepIndex = steps.findIndex((s) => s.id === currentStep);

  return (
    <div className="w-full max-w-2xl mx-auto">
      <div className="space-y-6">
        {steps.map((step, index) => {
          const Icon = step.icon;
          const isActive = index === currentStepIndex;
          const isComplete = index < currentStepIndex;
          const isPending = index > currentStepIndex;

          return (
            <motion.div
              key={step.id}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: index * 0.1 }}
              className="flex items-center gap-4"
            >
              <div
                className={`
                  w-12 h-12 rounded-full flex items-center justify-center transition-all
                  ${isComplete ? 'bg-green-500 text-white' : ''}
                  ${isActive ? 'bg-rose-500 text-white animate-pulse' : ''}
                  ${isPending ? 'bg-gray-200 text-gray-400' : ''}
                `}
              >
                <Icon className="w-6 h-6" />
              </div>

              <div className="flex-1">
                <h3
                  className={`
                    font-semibold text-sm transition-colors
                    ${isActive || isComplete ? 'text-gray-900' : 'text-gray-400'}
                  `}
                >
                  {step.label}
                </h3>
                <p
                  className={`
                    text-xs transition-colors
                    ${isActive ? 'text-gray-600' : 'text-gray-400'}
                  `}
                >
                  {step.description}
                </p>

                {isActive && progress > 0 && (
                  <div className="mt-2">
                    <div className="h-1.5 bg-gray-200 rounded-full overflow-hidden">
                      <motion.div
                        className="h-full bg-rose-500"
                        initial={{ width: 0 }}
                        animate={{ width: `${progress}%` }}
                        transition={{ duration: 0.3 }}
                      />
                    </div>
                    <p className="text-xs text-gray-500 mt-1">{progress}%</p>
                  </div>
                )}
              </div>

              {isComplete && (
                <motion.div
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  className="text-green-500"
                >
                  <FiCheck className="w-5 h-5" />
                </motion.div>
              )}
            </motion.div>
          );
        })}
      </div>

      <div className="mt-8 text-center">
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ duration: 2, repeat: Infinity, ease: 'linear' }}
          className="inline-block"
        >
          <FiMusic className="w-8 h-8 text-rose-500" />
        </motion.div>
        <p className="mt-3 text-gray-600 text-sm">
          Creating your personalized breakup song...
        </p>
      </div>
    </div>
  );
}

